import React from "react";
import PropTypes from 'prop-types'

function Card({ nombre, imagen, opiniones }) {
    const imagePath = require(`../assets/${imagen}`);
    return (
        <div>
            <div className="card text-center  animate__animated animate__fadeInUp">
                <div className="overflow">
                    <img src={imagePath} alt="a wallpaper" className="card-img-top" fluid="true" />
                </div>
                <div className="card-body ">
                    <h4 className="card-title">{nombre}</h4>
                    <p className="card-text text-secondary">{opiniones}</p>
                    <a href="https://example.com" className="btn btn-outline-secondary border-0">
                        Go to this website
                    </a>
                </div>
            </div>
        </div>

    )
}
Card.propTypes = {
    nombre: PropTypes.string.isRequired,
    imagen: PropTypes.string
}
export default Card;
