import React, { useEffect, useState } from 'react';
import './hoteles.css';
import Card from '../Card';

const Hoteles = ({ filtro }) => {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // const fetchData = async () => {
    //   try {
    //     const response = await fetch('http://localhost:3000/api/hoteles');
    //     if (!response.ok) {
    //       throw new Error('Error al obtener los datos');
    //     }
    //     const jsonData = await response.json();
    //     setData(jsonData);
    //     setIsLoading(false);
    //   } catch (error) {
    //     setError(error.message);
    //     setIsLoading(false);
    //   }
    // };

    const filterData = async () => {
      try {

        let filter = 'Bogo';
        const response = await fetch(`http://localhost:3000/api/hoteles/${filter}`);
        if (!response.ok) {
          throw new Error('Error al obtener los datos');
        }
        const jsonData = await response.json();
        setData(jsonData);
        setIsLoading(false);
      } catch (error) {
        setError(error.message);
        setIsLoading(false);
      }
    };

    // fetchData();
    filterData();
  }, []);

  if (isLoading) {
    return <div>Cargando...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    
      <div className="row">
        {data.map((hotel) => (
          <div className="col-md-4" key={hotel.id}>
            <Card nombre={hotel.nombre} imagen={hotel.imagen} opiniones={hotel.opiniones} url={hotel.url}/>
          </div>
        ))}
      </div>
    
  );
};

export default Hoteles;
