import React from "react";
import './nav.css';
import { filterData } from "./hoteles/hoteles";
import PropTypes from 'prop-types';

const NavBar = () => {
  const handleSearch = (event) => {
    const searchTerm = event.target.value;
    filterData(searchTerm);
  };

  return (
    <div className="container-fluid">
      <h2 className="navbar-h2">Hoteles</h2>
      <input
        placeholder="Search"
        className="form-control-lg navbar"
        onChange={handleSearch} // Llama a la función handleSearch cuando el valor del input cambia
      />
    </div>
  );
};

export default NavBar;
