import React, { useEffect, useState } from 'react';
import './searchComponent.css'
import Card from '../Card';
import Search from './componentes/searchComponent/searchComponent.js'

const Hoteles = () => {
    const [data, setData] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('http://localhost:3000/api/hoteles');
                if (!response.ok) {
                    throw new Error('Error al obtener los datos');
                }
                const jsonData = await response.json();
                console.log(jsonData);
                setData(jsonData);
                setIsLoading(false);
            } catch (error) {
                setError(error.message);
                setIsLoading(false);
            }
        };

        const filterData = async (filtro) => {
            try {
                const response = await fetch('http://localhost:3000/api/hoteles/filtro');
                if (!response.ok) {
                    throw new Error('Error al obtener los datos');
                }
                const jsonData = await response.json();
                console.log(jsonData);
                setData(jsonData);
                setIsLoading(false);
            } catch (error) {
                setError(error.message);
                setIsLoading(false);
            }
        };

        fetchData();
    }, []);

    if (isLoading) {
        return <div>Cargando...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <>
            <div className="container">
                <Search/>
            </div>
            <div className="container d-flex justify-content-center align-items-center h-100">
                <div className='row'>
                    {
                        data.map(hotel => (
                            <div className="col-md-4" key={hotel.id} >
                                <Card nombre={hotel.nombre} imagen={hotel.imagen} />
                            </div>
                        ))
                    }
                </div>
            </div>
        </>
    )
};

export default Hoteles;
